<?phpfunction sanitize($string, $trim = false,  $end_char = '&#8230;', $int = false, $str = false)    {        $string = filter_var($string, FILTER_SANITIZE_STRING);        $string = trim($string);        $string = stripslashes($string);        $string = strip_tags($string);        $string = str_replace(array('‘', '’', '“', '”'), array("'", "'", '"', '"'), $string);        if ($trim) {            if (strlen($string) < $trim)            {                return $string;            }            $string = preg_replace("/\s+/", ' ', str_replace(array("\r\n", "\r", "\n"), ' ', $string));            if (strlen($string) <= $trim)            {                return $string;            }            $out = "";            foreach (explode(' ', trim($string)) as $val)            {                $out .= $val.' ';                if (strlen($out) >= $trim)                {                    $out = trim($out);                    return (strlen($out) == strlen($string)) ? $out : $out.$end_char;                }            }            //$string = substr($string, 0, $trim);        }        if ($int)            $string = preg_replace("/[^0-9\s]/", "", $string);        if ($str)            $string = preg_replace("/[^a-zA-Z\s]/", "", $string);        return $string;    }	if(isset($_POST['host']) && isset($_POST['user']) && isset($_POST['password']) && isset($_POST['database']) && isset($_POST['newDB'])){define('DB_HOST', sanitize($_POST['host'])); // database locationdefine('DB_NAME', sanitize($_POST['database'])); // database namedefine('DB_USER', sanitize($_POST['user'])); // user namedefine('DB_PASS', sanitize($_POST['password'])); // password$conn = @mysqli_connect(DB_HOST, DB_USER, DB_PASS) or die("1");$db_selected = mysqli_select_db($conn, DB_NAME);if(!$db_selected ){if(sanitize($_POST['newDB']) == "1"){		$sql = 'CREATE DATABASE '.DB_NAME;	if (!$conn->query($sql)) die("3");		if(!mysqli_select_db($conn, DB_NAME)) die("4");	}else die ("2");//die("<div class='msgBox1'>MYSQL ERROR: Oops! Database access FAILED!</div>");}die ("5");}else die ("0");?>